window.CHECKIT_EXAMPLES=[{"name":"Check when <code>mywebsite.com</code> is down","code":"// send request to mywebsite.com\nrequest('http://mywebsite.com', function (err, resp, body) {\n  //check if response fails\n  if (resp.statusCode != 200) {\n    //send notification/email\n    notify('\"mywebsite.com\" is down')\n  }\n  done()\n})\n"},{"name":"Check for new posts on <code>martinfowler.com</code>","code":"// fetch RSS feed\nrequest('http://martinfowler.com/feed.atom', function(err, resp, body) {\n  if (resp.statusCode == 200) {\n    // get list of posts\n    parseXml(body, function(err, result) {\n      // date of last post\n      var lastPostDate = result.feed.entry[0].updated[0]\n      // is last post today?\n      if (moment(lastPostDate).isSame(new Date(), 'day')) {\n        // send notification/email\n        notify('New post on martinfowler.com')\n      }\n      done()\n    })\n  } else done()\n})\n"},{"name":"Check if it's freezing today","code":"// using openWeatherMap api\nvar city = 'Paris,fr'\nvar appid = 'API_KEY'\nvar url ='http://api.openweathermap.org/data/2.5/weather?q=' + city + '&appid=' + appid + '&units=metric'\n\n// request api for today's weather\nrequest({ url: url, json: true }, function(err, resp, body) {\n  // check if temperature below 0\n  if (resp.statusCode == 200 && body.main.temp_min < 0) {\n    // send notification/email\n    notify('Today is freezing')\n  }\n  done()\n})\n"}]